import React, {useEffect, useState} from 'react';
import Placeholder from './Placeholder';
import axios from 'axios';
import './canvas.css';


let hexToRgb = (hex) => {
    var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? {
      r: parseInt(result[1], 16),
      g: parseInt(result[2], 16),
      b: parseInt(result[3], 16)
    } : null;
  }



const Canvas = () => {
    const [loading, setLoading] = useState(false);
    const [url, setUrl] = useState("https://api.unsplash.com/");
    const [accessKey, setAccessKey] = useState("rFp4zeyJjvfOk8MSAtEemJl1PXFNPRYlM_fQL21vB-k");
    const [choice, setChoice] = useState(false);
    const [choiseList, setChoiceList] = useState([]);
    const [TopicRender1, setTopicRender1] = useState([]);
    const [TopicRender2, setTopicRender2] = useState([]);
    const [TopicRender3, setTopicRender3] = useState([]);
    const [TopicRenderOnce, setRenderOnce] = useState(false);
    const [topicList, setTopicList] = useState(["Animals", "programming", "Music", "Art", "Food", "Video Games", "Fashion", "historical", "Web Design", "Landscape", "Beautiful women", "Cars", "Hairstyles", "Cute", "Adventure", "Sports", "Cinema", "Culture", "Startup", "LifeStyle", "Dance"]);

    let makeChoiceList = (topic) => {
        console.log(topic)
        setChoiceList((prevState) => [...prevState, topic])
    }

    useEffect(() => {

        if(!choice && !TopicRenderOnce){
            topicList.map((value, index) => {
                //setLoading(true)
                axios.get(`${url}search/photos?page=1&client_id=${accessKey}&query=${value}`)
                    .then((res) => {
                        let rgb = hexToRgb(res.data.results[0].color);
                        let width = res.data.results[0].width;
                        let height = res.data.results[0].height;

                        let ar = width/height;
                        if(ar <= 0.56){
                            width = "16em";
                            height = "22.5em";
                        } 
                        else if(ar <= 0.71){
                            width = "16em";
                            height = "20em";
                        }
                        else if (ar <= 1){
                            width = "16em";
                            height = "16em";
                        }
                        else if(ar <= 1.25){
                            width = "16em";
                            height = "12em"
                        }
                        else{
                            width = "16em";
                            height = "10em";
                        }

                        let url_img = res.data.results[0].urls.regular;
                        let elem = <Placeholder id={index} addTopic={(val)=>makeChoiceList(val)} topic={value}  width={width} height={height} color={`${rgb.r}, ${rgb.g}, ${rgb.g}`} url={url_img}/>
                        if(index%3==0 && index!=0){
                            console.log("render3")
                            console.log(index)
                            setTopicRender3((prev)=>[...prev, elem])
                        }
                        else if(index%2==0 && index!=0){
                            console.log("render2")
                            console.log(index)
                            setTopicRender2((prev)=>[...prev, elem])
                        }
                        else{
                            console.log("render1")
                            console.log(index)
                            setTopicRender1((prev)=>[...prev, elem])
                        }

                    })

                return value;
            })
        }
        setRenderOnce(true)
    }, [])

    return (
        <>
        <div style={{display:"flex", width:"100vw", height:"6em", backgroundColor:"white", boxShadow: "0 8px 6px -6px #b0b0b0"}}>
            <div style={{flexGrow:2}}>
                <h2>
                    PhotoBooth
                </h2>
            </div>
            <div style={{flexGrow:8}}> </div>
            <div style={{flexgrow:2}}> </div>
        </div>
        <div className="Row">

            {
                loading?
                    <>
                    <div className="Column">
                        <Placeholder height={500} width={300} color={"253, 88, 0"}/>
                        <Placeholder height={400} width={300} color={"89, 171, 12"}/>
                        <Placeholder height={250} width={300} color={"253, 88, 0"}/>
                    </div>
                    <div className="Column">
                        <Placeholder height={500} width={300} color={"142, 4, 189"}/>
                        <Placeholder height={250} width={300} color={"253, 88, 0"}/>
                        <Placeholder height={500} width={300} color={"142, 4, 189"}/>
                    </div>
                    <div className="Column">
                        <Placeholder height={400} width={300}  color={"253, 88, 0"}/>
                        <Placeholder height={500} width={300} color={"89, 171, 12"}/>
                        <Placeholder height={250} width={300} color={"253, 88, 0"}/>
                    </div>
                    <div className="Column">
                        <Placeholder height={250} width={300} color={"89, 171, 12"}/>
                        <Placeholder height={500} width={300} color={"253, 88, 0"}/>
                        <Placeholder height={500} width={300} color={"142, 4, 189"}/>
                    </div>
                    <div className="Column">
                        <Placeholder height={400} width={300} color={"156, 0, 31"}/>
                        <Placeholder height={250} width={300} color={"253, 88, 0"}/>
                        <Placeholder height={500} width={300} color={"142, 4, 189"}/>
                    </div>
                    </>
                :
                <>
                    {
                        !choice?
                            <div className="First-card">
                                <div>
                                    <h2>Select your favourite topics</h2>
                                </div>
                                <div className="Row2">
                                    <div className="Column">
                                       {TopicRender1}
                                    </div>
                                    <div className="Column">
                                        {TopicRender2}
                                    </div>
                                    <div>
                                        {TopicRender3}
                                    </div>
                                </div>
                            </div>
                            :
                            <div>

                            </div>  

                    }
                </>
            }
        </div>
    </>
    );
};

export default Canvas;
